  <title>Complex Dashboard Prototype</title>
  <style>
    :root {
      --primary: #2c3e50;
      --secondary: #ecf0f1;
      --accent: #3498db;
      --light: #ffffff;
      --dark: #34495e;
      --spacing: 1rem;
    }
    * { box-sizing: border-box; margin: 0; padding: 0; }
    body {
      font-family: Arial, sans-serif;
      background: var(--secondary);
      color: var(--primary);
      line-height: 1.5;
    }
    .container {
      display: grid;
      grid-template-areas:
        "header header"
        "sidebar main"
        "footer footer";
      grid-template-columns: 250px 1fr;
      grid-template-rows: auto 1fr auto;
      gap: var(--spacing);
      max-width: 1440px;
      margin: var(--spacing) auto;
    }
    header {
      grid-area: header;
      background: var(--light);
      padding: var(--spacing);
      display: flex;
      justify-content: space-between;
      align-items: center;
      border-bottom: 1px solid var(--dark);
    }
    header h1 { font-size: 1.5rem; color: var(--accent); }
    header nav select,
    header nav input[type="text"] {
      margin-left: var(--spacing);
      padding: 0.5em;
      border: 1px solid var(--dark);
      border-radius: 4px;
    }
    aside {
      grid-area: sidebar;
      background: var(--light);
      padding: var(--spacing);
      border-right: 1px solid var(--dark);
    }
    aside nav ul {
      list-style: none;
    }
    aside nav li {
      margin: 0.5rem 0;
    }
    aside nav a {
      text-decoration: none;
      color: var(--primary);
      padding: 0.5em;
      display: block;
      border-radius: 4px;
    }
    aside nav a:hover {
      background: var(--accent);
      color: var(--light);
    }
    main {
      grid-area: main;
      display: grid;
      grid-template-rows: auto auto auto auto;
      gap: var(--spacing);
    }
    .filters, .cards, .tables, .form-section, .nested-grid {
      background: var(--light);
      padding: var(--spacing);
      border-radius: 8px;
      box-shadow: 0 2px 5px rgba(0,0,0,0.1);
    }
    .filters {
      display: flex;
      flex-wrap: wrap;
      gap: var(--spacing);
    }
    .filters label {
      display: flex;
      flex-direction: column;
      font-size: 0.9rem;
    }
    .filters select, .filters input {
      margin-top: 0.25rem;
      padding: 0.5em;
      border: 1px solid var(--dark);
      border-radius: 4px;
    }
    .cards {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
      gap: var(--spacing);
    }
    .card {
      background: var(--secondary);
      padding: var(--spacing);
      border-radius: 8px;
      text-align: center;
    }
    .tables table {
      width: 100%;
      border-collapse: collapse;
      margin-bottom: var(--spacing);
    }
    .tables th, .tables td {
      border: 1px solid var(--dark);
      padding: 0.5em;
      text-align: left;
    }
    .tables th {
      background: var(--accent);
      color: var(--light);
      position: sticky;
      top: 0;
    }
    .tables tr:nth-child(even) {
      background: var(--secondary);
    }
    .form-section form {
      display: grid;
      grid-template-columns: 1fr 1fr;
      gap: var(--spacing);
    }
    .form-section fieldset {
      border: 1px solid var(--dark);
      border-radius: 4px;
      padding: var(--spacing);
    }
    .form-section legend {
      font-weight: bold;
      padding: 0 0.25em;
    }
    .form-section label {
      display: flex;
      align-items: center;
      gap: 0.5em;
      margin-bottom: var(--spacing);
    }
    .nested-grid {
      display: grid;
      grid-template-columns: 2fr 1fr;
      gap: var(--spacing);
    }
    .nested-grid .sub-grid {
      display: grid;
      grid-template-columns: 1fr 1fr;
      grid-template-rows: 1fr 1fr;
      gap: var(--spacing);
    }
    footer {
      grid-area: footer;
      text-align: center;
      padding: var(--spacing);
      background: var(--light);
      border-top: 1px solid var(--dark);
      font-size: 0.85rem;
    }
    .range-control {
      display: flex;
      flex-direction: column;
      gap: 0.25em;
    }
  </style>
</head>
