    .card {
      background: var(--card-bg);
      padding: var(--gap);
      border-radius: var(--border-radius);
      box-shadow: 0 1px 4px rgba(0,0,0,0.1);
    }
    h2 { margin-bottom: 0.5rem; font-weight: 600; }
    /* Grids */
    .grid-2 {
      display: grid;
