class Solution:

	def bellman_ford(self, V, edges, S):
		dis = [100000000 for i in range(V)]
		dis[S] = 0
		for i in range(V - 1):
			for (m, n, o) in edges:
				if dis[m] != 100000000 and dis[m] + o < dis[n]:
					dis[n] = dis[m] + o
		for (m, n, o) in edges:
			if dis[m] != 100000000 and dis[m] + o < dis[n]:
				return [-1]
		return dis

