class Solution:

	def findFirstNode(self, head):
		if not head and (not head.next):
			return -1
		hashMap = {}
		curr = head
		while curr:
			isExists = hashMap.get(curr, None)
			if isExists:
				return curr.data
			hashMap[curr] = 1
			curr = curr.next
		return -1

